import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
let PaypalPaymentsComponent = class PaypalPaymentsComponent {
    constructor() {
        this.product = {
            price: 17.99,
            description: 'Pakiet podstawowy oferuje spersonalizowany plan treningowy',
            img: '../../assets/img/main-bg.jpg'
        };
        this.paidFor = false;
    }
    ngOnInit() {
        paypal
            .Buttons({
            createOrder: (data, actions) => {
                return actions.order.create({
                    purchase_units: [{
                            description: this.product.description,
                            amount: {
                                currency_code: 'USD',
                                value: this.product.price
                            }
                        }
                    ]
                });
            },
            onApprove: async (data, actions) => {
                const order = await actions.order.capture();
                this.paidFor = true;
                console.log(order);
            },
            onError: err => {
                console.log(err);
            }
        })
            .render(this.paypalElement.nativeElement);
    }
};
__decorate([
    ViewChild('paypal', { static: true })
], PaypalPaymentsComponent.prototype, "paypalElement", void 0);
PaypalPaymentsComponent = __decorate([
    Component({
        selector: 'app-paypal-payments',
        templateUrl: './paypal-payments.component.html',
        styleUrls: ['./paypal-payments.component.css']
    })
], PaypalPaymentsComponent);
export { PaypalPaymentsComponent };
